
public class printpattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

				
		int k=5;
		for(int i=0;i<5;i++)//3
		{
			

			for(int j=0;j<5-i;j++)
				{
					System.out.print(k+" ");
				}
			k--;
			System.out.println(" ");
		}


		}
	}


