
public class swapvarwithouttemp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=50;
		int b=90;
		
		a=a+b;//50+90=140
		b=a-b;//140-90=50
		a=a-b;//140-50=90
		
		System.out.println("value of a="+ a);
		System.out.println("value of b="+ b);

		

	}

}
